import { HttpClient } from "@angular/common/http";
import { Inject } from "@angular/core";
import { catchError, map, throwError as observableThrowError } from "rxjs";


export class ContactService {
  constructor(public http: HttpClient, @Inject('BASE_URL') public baseUrl: string) {
   
  }

  createContact(model: any) {
    let createContactURL = this.baseUrl +"WeatherForecastControllerAddContact/"
    return this.http.post(createContactURL, JSON.stringify(model))
      .pipe(
        map((res) => {
          
          return res;
        }),
        catchError(result => {
          return this.handleError(result);
        })
      );
  }
  
  private handleError(result: any) {

    if (result.status === 400 || result.status === 404) {
      console.log("in 400")
      console.log(result);
         
      return observableThrowError(result);
    }
    if (result.status === 500) {
      console.log("in 500")
      console.log(result);
    
      return observableThrowError(result);
    }
    if (result.status === 403) {

      console.log("in 403")
      console.log(result);
    }
   
   if (result.status !== 401) {
      
    }


    if (result.headers === undefined) {
      return observableThrowError(result);
    }

    const applicationError = result.headers.get('Application-Error');
    return observableThrowError(applicationError || 'Server error');
  }
}
