import { AfterViewInit, Component, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { ContactService } from '../Services/contact.service';
import { FormControl, FormGroup } from '@angular/forms';
import { ModalDirective } from 'ngx-bootstrap/modal';
import { ValidationHelper } from '../Utility/validation-helper';
import { ContactDto } from '../Modal/ContactDto';

@Component({
  selector: 'app-create-contact',
  templateUrl: './create-contact.component.html',
  styleUrls: ['./create-contact.component.css']
})
export class CreateContactComponent implements OnInit, AfterViewInit {
  @Input() selectedContactId: number = 0;
  contactForm!: FormGroup;
  @Output() saveClick = new EventEmitter();
  @Output() closeClick = new EventEmitter();
  @ViewChild('contactModal', { static: true }) contactModal!: ModalDirective;
  constructor(
    private readonly contactService: ContactService) {

  }
  ngOnInit() {

  }
  ngOnChanges() {
    this.contactForm = new FormGroup({
      email: new FormControl(),
      firstName: new FormControl(),
      lastName: new FormControl(),

    });
    if (this.selectedContactId > 0) {
      this.contactService.getContactsById(this.selectedContactId).subscribe(x => {
        this.contactForm.patchValue({
          email: x.email,
          firstName: x.firstName,
          lastName: x.lastName
        })
      })
      if (this.contactModal) {
        this.contactModal.show();
      }
    }
    else {
      if (this.contactModal) {
        this.contactModal.show();
      }
    }
    
  }
  ngAfterViewInit() { 
      this.openModal(); 
  }
  openModal() {
    this.contactModal.show();
  }

  closeModal() {
    this.selectedContactId = 0;
    this.contactModal.hide();
    this.closeClick.emit(true);
  }


  saveContactForm() {
    if (this.contactForm.invalid) {
      ValidationHelper.validateAllFormFields(this.contactForm);
      return;
    }
    else {
      let contactDto: ContactDto = new ContactDto();
      contactDto = Object.assign({}, this.contactForm.value);
      if (this.selectedContactId > 0)
        contactDto.contactId = this.selectedContactId;
      else
        contactDto.contactId = 0;

    }
  }
  createContact(data: ContactDto) {
    this.contactService.createContact(data).subscribe(x => {
      this.saveClick.emit(true);
    })
  }

  updateContact(data: ContactDto) {
    this.contactService.createContact(data).subscribe(x => {
      this.saveClick.emit(true);
    })
  }
}

<div bsModal #contactModal="bs-modal" class="modal" id="contactModal" tabindex="-1" role="dialog" aria-labelledby="contactModal" aria-hidden="true">
  <div class="modal-dialog modal-lg">
    <div class="modal-content">
       
      <div class="modal-header">
        <button type="button" class="close" data-dismiss="modal" (click)="closeModal()">&times;</button>
        <h4 class="modal-title">Modal Header</h4>
      </div>

      <div class="modal-body">

        <form [formGroup]="contactForm">
          <div class="form-group">
            <label for="email">Email address</label>
            <input type="email"
                   class="form-control"
                   id="emailId"
                   placeholder="Enter email"
                   formControlName="email">

          </div>
          <div class="form-group">
            <label for="firstName">First Name</label>
            <input type="text"
                   class="form-control"
                   id="firstNameId"
                   placeholder="Enter First Name"
                   formControlName="firstName">
          </div>
          <div class="form-group">
            <label for="lastName">Last Name</label>
            <input type="text"
                   class="form-control"
                   id="lastNameId"
                   placeholder="Enter Last Name"
                   formControlName="lastName">
          </div>

        </form>

      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary btn-sm" (click)="saveContactForm()">
          Save
        </button>
        <button type="button" class="btn btn-secondary btn-sm" (click)="closeModal()">
          Close
        </button>

      </div>

    </div>
  </div>
</div>
